SELECT businessTable.name from businessTable join restaurantTable on businessTable.business_id = restaurantTable.business_id join checkinTable on businessTable.business_id = checkinTable.b_id where businessTable.postal_code='89121' and CONVERT(datetime,checkinTable.date) between CAST('2016-01-01' as date) and CAST('2017-01-01' as date) group by businessTable.business_id,businessTable.name order by sum(checkinTable.occurence) DESC;
